import React, { useState, useRef, useEffect } from 'react';
import './ArticleEditor.css';

interface ArticleData {
  title: string;
  content: string;
  excerpt: string;
  category: string;
  author: string;
  image: string;
  imageUrl?: string;
  readTime: string;
}

interface ArticleEditorProps {
  onSave: (article: ArticleData) => void;
  onCancel: () => void;
  initialData?: Partial<ArticleData>;
}

const ArticleEditor: React.FC<ArticleEditorProps> = ({ onSave, onCancel, initialData }) => {
  const [article, setArticle] = useState<ArticleData>({
    title: '',
    content: '',
    excerpt: '',
    category: 'Engineering',
    author: 'Guest Author',
    image: '‚úçÔ∏è',
    imageUrl: '',
    readTime: '5 min read',
    ...initialData
  });

  const [isPreview, setIsPreview] = useState(false);
  const [imagePreview, setImagePreview] = useState<string>('');
  const [showImageInserter, setShowImageInserter] = useState(false);
  const [showFormatHelp, setShowFormatHelp] = useState(false);
  const [activeFormat, setActiveFormat] = useState<string>('');
  const [isDragging, setIsDragging] = useState(false);
  const [autoSave, setAutoSave] = useState(false);
  const [isWysiwygMode, setIsWysiwygMode] = useState(true);

  const textareaRef = useRef<HTMLTextAreaElement>(null);
  const wysiwygRef = useRef<HTMLDivElement>(null);
  const contentImageInputRef = useRef<HTMLInputElement>(null);

  const categories = [
    'Engineering',
    'Systems',
    'Architecture',
    'Performance',
    'AI/ML',
    'Design',
    'Trends',
    'UI/UX'
  ];

  // Computed values
  const isFormValid = article.title.trim() && article.content.trim() && article.excerpt.trim();

  // Auto-save functionality
  useEffect(() => {
    if (autoSave) {
      const timer = setTimeout(() => {
        if (article.title.trim() && article.content.trim()) {
          // Could implement auto-save to localStorage here
          console.log('Auto-saving...', article);
        }
      }, 5000);
      return () => clearTimeout(timer);
    }
  }, [article, autoSave]);

  // Keyboard shortcuts
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (e.ctrlKey || e.metaKey) {
        if (e.key === 'b') {
          e.preventDefault();
          insertFormatting('bold');
        } else if (e.key === 'i') {
          e.preventDefault();
          insertFormatting('italic');
        } else if (e.key === 's') {
          e.preventDefault();
          if (isFormValid) {
            handleSave();
          }
        }
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [article, isFormValid]);

  const handleInputChange = (field: keyof ArticleData, value: string) => {
    setArticle(prev => ({ ...prev, [field]: value }));
  };

  const handleImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const result = e.target?.result as string;
        setImagePreview(result);
        setArticle(prev => ({ ...prev, imageUrl: result }));
      };
      reader.readAsDataURL(file);
    }
  };

  const calculateReadTime = (content: string) => {
    const wordsPerMinute = 200;
    const words = content.trim().split(/\s+/).length;
    const minutes = Math.ceil(words / wordsPerMinute);
    return `${minutes} min read`;
  };

  const handleContentChange = (content: string) => {
    const readTime = calculateReadTime(content);
    setArticle(prev => ({ ...prev, content, readTime }));
  };

  // Convert markdown to display format for WYSIWYG
  const markdownToDisplay = (markdown: string) => {
    if (!isWysiwygMode) return markdown;

    return markdown
      .replace(/\*\*(.*?)\*\*/g, 'ùóï$1ùóï') // Bold markers
      .replace(/(?<!\*)\*([^*]+)\*(?!\*)/g, 'ÔøΩ$1ÔøΩ') // Italic markers (avoid double asterisks)
      .replace(/^## (.+)$/gm, 'ùóõ$1ùóõ') // Heading markers
      .replace(/^> (.+)$/gm, '‚ùù$1‚ùû') // Quote markers
      .replace(/^- (.+)$/gm, '‚Ä¢ $1') // List markers
      .replace(/<div style="text-align:\s*center">(.*?)<\/div>/g, '‚äû$1‚äû') // Center markers
      .replace(/<div style="text-align:\s*justify">(.*?)<\/div>/g, '‚â°$1‚â°'); // Justify markers
  };

  // Convert display format back to markdown
  const displayToMarkdown = (display: string) => {
    if (!isWysiwygMode) return display;

    return display
      .replace(/ùóï(.*?)ùóï/g, '**$1**') // Bold
      .replace(/ÔøΩ(.*?)ÔøΩ/g, '*$1*') // Italic
      .replace(/^ùóõ(.+)ùóõ$/gm, '## $1') // Heading
      .replace(/^‚ùù(.+)‚ùû$/gm, '> $1') // Quote
      .replace(/^‚Ä¢ (.+)$/gm, '- $1') // List
      .replace(/‚äû(.*?)‚äû/g, '<div style="text-align: center">$1</div>') // Center
      .replace(/‚â°(.*?)‚â°/g, '<div style="text-align: justify">$1</div>'); // Justify
  };

  // Get display content for textarea
  const getDisplayContent = () => {
    return isWysiwygMode ? markdownToDisplay(article.content) : article.content;
  };

  // Handle content change with format conversion
  const handleDisplayContentChange = (displayContent: string) => {
    const markdownContent = displayToMarkdown(displayContent);
    const readTime = calculateReadTime(markdownContent);
    setArticle(prev => ({ ...prev, content: markdownContent, readTime }));
  };

  // Get current selection in textarea
  const getCurrentSelection = () => {
    if (textareaRef.current) {
      const start = textareaRef.current.selectionStart;
      const end = textareaRef.current.selectionEnd;
      const displayContent = getDisplayContent();
      const selected = displayContent.substring(start, end);
      return { start, end, selected };
    }
    return { start: 0, end: 0, selected: '' };
  };

  // Enhanced formatting with WYSIWYG support
  const insertFormatting = (format: string) => {
    if (!textareaRef.current) return;

    const { start, end, selected } = getCurrentSelection();
    const displayContent = getDisplayContent();
    const beforeText = displayContent.substring(0, start);
    const afterText = displayContent.substring(end);

    let newText = '';
    let cursorOffset = 0;

    // Show active format temporarily
    setActiveFormat(format);
    setTimeout(() => setActiveFormat(''), 200);

    if (isWysiwygMode) {
      // WYSIWYG mode - use visual markers
      switch (format) {
        case 'bold':
          newText = selected ? `ùóï${selected}ùóï` : 'ùóïBold Textùóï';
          cursorOffset = selected ? 0 : 1;
          break;
        case 'italic':
          newText = selected ? `ÔøΩ${selected}ÔøΩ` : 'ÔøΩItalic TextÔøΩ';
          cursorOffset = selected ? 0 : 1;
          break;
        case 'heading':
          // For headings, we need to handle line breaks properly
          const headingText = selected ? selected : 'Heading';
          newText = `ùóõ${headingText}ùóõ`;
          cursorOffset = selected ? 0 : 1;
          break;
        case 'center':
          newText = selected ? `‚äû${selected}‚äû` : '‚äûCentered Text‚äû';
          cursorOffset = selected ? 0 : 1;
          break;
        case 'justify':
          newText = selected ? `‚â°${selected}‚â°` : '‚â°Justified Text‚â°';
          cursorOffset = selected ? 0 : 1;
          break;
        case 'quote':
          // For quotes, we need to handle line breaks properly
          const quoteText = selected ? selected : 'Quote Text';
          newText = `‚ùù${quoteText}‚ùû`;
          cursorOffset = selected ? 0 : 1;
          break;
        case 'list':
          newText = selected ? `‚Ä¢ ${selected}` : '‚Ä¢ List Item';
          cursorOffset = selected ? 0 : 2;
          break;
        default:
          return;
      }
    } else {
      // Markdown mode - use raw markdown
      switch (format) {
        case 'bold':
          newText = selected ? `**${selected}**` : '**Bold Text**';
          cursorOffset = selected ? 0 : 2;
          break;
        case 'italic':
          newText = selected ? `*${selected}*` : '*Italic Text*';
          cursorOffset = selected ? 0 : 1;
          break;
        case 'heading':
          // Ensure heading is on its own line
          const headingPrefix = beforeText.endsWith('\n') || beforeText === '' ? '' : '\n';
          const headingSuffix = afterText.startsWith('\n') || afterText === '' ? '' : '\n';
          newText = `${headingPrefix}## ${selected || 'Heading'}${headingSuffix}`;
          cursorOffset = selected ? 0 : 3;
          break;
        case 'center':
          newText = selected ? `<div style="text-align: center">${selected}</div>` : '<div style="text-align: center">Centered Text</div>';
          cursorOffset = selected ? 0 : 30;
          break;
        case 'justify':
          newText = selected ? `<div style="text-align: justify">${selected}</div>` : '<div style="text-align: justify">Justified Text</div>';
          cursorOffset = selected ? 0 : 31;
          break;
        case 'quote':
          // Ensure quote is on its own line
          const quotePrefix = beforeText.endsWith('\n') || beforeText === '' ? '' : '\n';
          const quoteSuffix = afterText.startsWith('\n') || afterText === '' ? '' : '\n';
          newText = `${quotePrefix}> ${selected || 'Quote Text'}${quoteSuffix}`;
          cursorOffset = selected ? 0 : 2;
          break;
        case 'list':
          // Ensure list item is on its own line
          const listPrefix = beforeText.endsWith('\n') || beforeText === '' ? '' : '\n';
          const listSuffix = afterText.startsWith('\n') || afterText === '' ? '' : '\n';
          newText = `${listPrefix}- ${selected || 'List Item'}${listSuffix}`;
          cursorOffset = selected ? 0 : 2;
          break;
        default:
          return;
      }
    }

    const newDisplayContent = beforeText + newText + afterText;
    const newMarkdownContent = displayToMarkdown(newDisplayContent);
    const readTime = calculateReadTime(newMarkdownContent);
    setArticle(prev => ({ ...prev, content: newMarkdownContent, readTime }));

    // Reset cursor position
    setTimeout(() => {
      textareaRef.current?.focus();
      const newPosition = selected ? start + newText.length : start + newText.length - cursorOffset;
      textareaRef.current?.setSelectionRange(newPosition, newPosition);
    }, 0);
  };

  // Drag and drop functionality
  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(false);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(false);

    const files = Array.from(e.dataTransfer.files);
    const imageFile = files.find(file => file.type.startsWith('image/'));

    if (imageFile) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const result = e.target?.result as string;
        insertImageIntoContent(result);
      };
      reader.readAsDataURL(imageFile);
    }
  };

  const handleContentImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const result = e.target?.result as string;
        insertImageIntoContent(result);
        setShowImageInserter(false);
      };
      reader.readAsDataURL(file);
    }
  };

  const insertImageIntoContent = (imageUrl: string) => {
    if (!textareaRef.current) return;

    const { start } = getCurrentSelection();
    const beforeText = article.content.substring(0, start);
    const afterText = article.content.substring(start);

    const imageMarkdown = `\n![Image](${imageUrl})\n`;
    const newContent = beforeText + imageMarkdown + afterText;

    setArticle(prev => ({ ...prev, content: newContent }));

    setTimeout(() => {
      textareaRef.current?.focus();
      textareaRef.current?.setSelectionRange(start + imageMarkdown.length, start + imageMarkdown.length);
    }, 0);
  };

  const handleSave = () => {
    if (article.title.trim() && article.content.trim()) {
      onSave(article);
    }
  };

  const toggleAutoSave = () => {
    setAutoSave(!autoSave);
  };

  return (
    <div className="article-editor">
      <div className="editor-header">
        <h2>Create New Article</h2>
        <div className="editor-actions">
          <button 
            className={`wysiwyg-btn ${isWysiwygMode ? 'active' : ''}`}
            onClick={() => setIsWysiwygMode(!isWysiwygMode)}
            title="Toggle WYSIWYG mode"
          >
            {isWysiwygMode ? '‚ú® Visual' : 'üìù Markdown'}
          </button>
          <button 
            className={`auto-save-btn ${autoSave ? 'active' : ''}`}
            onClick={toggleAutoSave}
            title="Toggle auto-save"
          >
            {autoSave ? 'üíæ Auto-save ON' : 'üíæ Auto-save OFF'}
          </button>
          <button 
            className={`preview-btn ${isPreview ? 'active' : ''}`}
            onClick={() => setIsPreview(!isPreview)}
          >
            {isPreview ? 'üìù Edit' : 'üëÅÔ∏è Preview'}
          </button>
          <button className="cancel-btn" onClick={onCancel}>
            Cancel
          </button>
          <button 
            className="save-btn" 
            onClick={handleSave}
            disabled={!isFormValid}
          >
            üíæ Save Article
          </button>
        </div>
      </div>

      {!isPreview ? (
        <div className="editor-form">
          <div className="form-row">
            <div className="form-group">
              <label htmlFor="title">Title</label>
              <input
                id="title"
                type="text"
                value={article.title}
                onChange={(e) => handleInputChange('title', e.target.value)}
                placeholder="Enter article title..."
                className="title-input"
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="excerpt">Excerpt</label>
              <textarea
                id="excerpt"
                value={article.excerpt}
                onChange={(e) => handleInputChange('excerpt', e.target.value)}
                placeholder="Brief description of the article..."
                className="excerpt-input"
                rows={3}
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="category">Category</label>
              <select
                id="category"
                value={article.category}
                onChange={(e) => handleInputChange('category', e.target.value)}
                className="category-select"
              >
                {categories.map(cat => (
                  <option key={cat} value={cat}>{cat}</option>
                ))}
              </select>
            </div>
            <div className="form-group">
              <label htmlFor="author">Author</label>
              <input
                id="author"
                type="text"
                value={article.author}
                onChange={(e) => handleInputChange('author', e.target.value)}
                placeholder="Author name..."
                className="author-input"
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="emoji">Emoji (if no image)</label>
              <input
                id="emoji"
                type="text"
                value={article.image}
                onChange={(e) => handleInputChange('image', e.target.value)}
                placeholder="üìù"
                className="emoji-input"
                maxLength={2}
              />
            </div>
            <div className="form-group">
              <label htmlFor="image">Upload Image</label>
              <input
                id="image"
                type="file"
                accept="image/*"
                onChange={handleImageUpload}
                className="image-input"
              />
              {imagePreview && (
                <div className="image-preview">
                  <img src={imagePreview} alt="Preview" />
                </div>
              )}
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="content">Article Content</label>
            <div className="content-section">
              <div className="content-toolbar">
                <div className="toolbar-group">
                  <button 
                    type="button" 
                    onClick={() => insertFormatting('bold')} 
                    className={`format-btn ${activeFormat === 'bold' ? 'active' : ''}`}
                    title="Bold (Ctrl+B)"
                  >
                    <strong>B</strong>
                  </button>
                  <button 
                    type="button" 
                    onClick={() => insertFormatting('italic')} 
                    className={`format-btn ${activeFormat === 'italic' ? 'active' : ''}`}
                    title="Italic (Ctrl+I)"
                  >
                    <em>I</em>
                  </button>
                  <button 
                    type="button" 
                    onClick={() => insertFormatting('heading')} 
                    className={`format-btn ${activeFormat === 'heading' ? 'active' : ''}`}
                    title="Heading"
                  >
                    <span className="heading-icon">H</span>
                  </button>
                </div>
                <div className="toolbar-separator"></div>
                <div className="toolbar-group">
                  <button 
                    type="button" 
                    onClick={() => insertFormatting('center')} 
                    className={`format-btn ${activeFormat === 'center' ? 'active' : ''}`}
                    title="Center Align"
                  >
                    <span className="align-icon">‚äû</span>
                  </button>
                  <button 
                    type="button" 
                    onClick={() => insertFormatting('justify')} 
                    className={`format-btn ${activeFormat === 'justify' ? 'active' : ''}`}
                    title="Justify"
                  >
                    <span className="align-icon">‚â°</span>
                  </button>
                </div>
                <div className="toolbar-separator"></div>
                <div className="toolbar-group">
                  <button 
                    type="button" 
                    onClick={() => insertFormatting('quote')} 
                    className={`format-btn ${activeFormat === 'quote' ? 'active' : ''}`}
                    title="Quote"
                  >
                    <span className="quote-icon">"</span>
                  </button>
                  <button 
                    type="button" 
                    onClick={() => insertFormatting('list')} 
                    className={`format-btn ${activeFormat === 'list' ? 'active' : ''}`}
                    title="List"
                  >
                    <span className="list-icon">‚Ä¢</span>
                  </button>
                </div>
                <div className="toolbar-separator"></div>
                <div className="toolbar-group">
                  <button 
                    type="button" 
                    onClick={() => setShowImageInserter(!showImageInserter)} 
                    className={`format-btn image-btn ${showImageInserter ? 'active' : ''}`}
                    title="Insert Image"
                  >
                    üñºÔ∏è
                  </button>
                  <button 
                    type="button" 
                    onClick={() => setShowFormatHelp(!showFormatHelp)} 
                    className={`format-btn help-btn ${showFormatHelp ? 'active' : ''}`}
                    title="Formatting Help"
                  >
                    ‚ùì
                  </button>
                </div>
              </div>

              {showFormatHelp && (
                <div className="format-help">
                  <h4>üìù Formatting Guide - {isWysiwygMode ? 'Visual Mode' : 'Markdown Mode'}</h4>
                  <div className="help-grid">
                    <div className="help-item">
                      <strong>Bold:</strong> {isWysiwygMode ? 'Shows as ùóïtextùóï' : '**text**'} or select text and click B
                    </div>
                    <div className="help-item">
                      <strong>Italic:</strong> {isWysiwygMode ? 'Shows as ÔøΩtextÔøΩ' : '*text*'} or select text and click I
                    </div>
                    <div className="help-item">
                      <strong>Heading:</strong> {isWysiwygMode ? 'Shows as ùóõtextùóõ' : '## text'} or select text and click H
                    </div>
                    <div className="help-item">
                      <strong>Quote:</strong> {isWysiwygMode ? 'Shows as ‚ùùtext‚ùû' : '> text'} or select text and click "
                    </div>
                    <div className="help-item">
                      <strong>List:</strong> {isWysiwygMode ? 'Shows as ‚Ä¢ text' : '- text'} or select text and click ‚Ä¢
                    </div>
                    <div className="help-item">
                      <strong>Center:</strong> {isWysiwygMode ? 'Shows as ‚äûtext‚äû' : '<div style="text-align: center">text</div>'}
                    </div>
                    <div className="help-item">
                      <strong>Justify:</strong> {isWysiwygMode ? 'Shows as ‚â°text‚â°' : '<div style="text-align: justify">text</div>'}
                    </div>
                    <div className="help-item">
                      <strong>Images:</strong> Drag & drop or click üñºÔ∏è to insert at cursor position
                    </div>
                    <div className="help-item">
                      <strong>Toggle Mode:</strong> Use ‚ú® Visual / üìù Markdown button to switch editing modes
                    </div>
                    <div className="help-item">
                      <strong>Shortcuts:</strong> Ctrl+B (Bold), Ctrl+I (Italic), Ctrl+S (Save)
                    </div>
                  </div>
                </div>
              )}

              {showImageInserter && (
                <div className="image-inserter">
                  <input
                    ref={contentImageInputRef}
                    type="file"
                    accept="image/*"
                    onChange={handleContentImageUpload}
                    className="content-image-input"
                  />
                  <span className="image-inserter-text">
                    üì∏ Select an image to insert into the article at cursor position
                  </span>
                </div>
              )}

              <div 
                className={`content-input-container ${isDragging ? 'dragging' : ''}`}
                onDragOver={handleDragOver}
                onDragLeave={handleDragLeave}
                onDrop={handleDrop}
              >
                <textarea
                  ref={textareaRef}
                  id="content"
                  value={getDisplayContent()}
                  onChange={(e) => handleDisplayContentChange(e.target.value)}
                  placeholder={isWysiwygMode ? 
                    `Write your article content here...

üí° Visual Mode Tips:
‚Ä¢ Select text and use formatting buttons for rich formatting
‚Ä¢ Drag & drop images directly into the editor
‚Ä¢ Use keyboard shortcuts: Ctrl+B for bold, Ctrl+I for italic
‚Ä¢ Formatting appears with visual indicators instead of markdown syntax` :
                    `Write your article content here...

üí° Markdown Mode Tips:
‚Ä¢ Use **bold** and *italic* syntax
‚Ä¢ Use ## for headings and > for quotes
‚Ä¢ Drag & drop images directly into the editor
‚Ä¢ Use keyboard shortcuts: Ctrl+B for bold, Ctrl+I for italic`}
                  className={`content-input ${isWysiwygMode ? 'wysiwyg-mode' : 'markdown-mode'}`}
                  rows={15}
                />
                {isDragging && (
                  <div className="drag-overlay">
                    <div className="drag-message">
                      <span className="drag-icon">üìÅ</span>
                      <span>Drop images here to insert them</span>
                    </div>
                  </div>
                )}
              </div>
            </div>
            <div className="content-meta">
              <span className="read-time">üìñ {article.readTime}</span>
              <span className="word-count">üìù {article.content.trim().split(/\s+/).length} words</span>
              <span className="char-count">üî§ {article.content.length} characters</span>
            </div>
          </div>
        </div>
      ) : (
        <div className="article-preview">
          <div className="preview-card">
            {article.imageUrl ? (
              <div className="preview-image" style={{ backgroundImage: `url(${article.imageUrl})` }} />
            ) : (
              <div className="preview-default-image">
                <span className="preview-emoji">{article.image}</span>
              </div>
            )}
            <div className="preview-content">
              <h3 className="preview-title">{article.title || 'Article Title'}</h3>
              <p className="preview-excerpt">{article.excerpt || 'Article excerpt will appear here...'}</p>
              <div className="preview-category">
                <span className="category-badge">{article.category}</span>
              </div>
              <div className="preview-meta">
                <span>{new Date().toLocaleDateString()}</span>
                <span>{article.readTime}</span>
                <span>By {article.author}</span>
              </div>
            </div>
          </div>
          <div className="preview-full-content">
            <h2>Full Article Content</h2>
            <div className="preview-article-content">
              {article.content.split('\n').map((paragraph, index) => {
                // Handle images
                if (paragraph.includes('![Image](')) {
                  const imageMatch = paragraph.match(/!\[Image\]\((.*?)\)/);
                  if (imageMatch) {
                    return (
                      <div key={index} className="content-image-container">
                        <img src={imageMatch[1]} alt="Article content" className="content-image" />
                      </div>
                    );
                  }
                }

                // Handle headings
                if (paragraph.startsWith('## ')) {
                  return <h3 key={index} className="content-heading">{paragraph.substring(3)}</h3>;
                }

                // Handle quotes
                if (paragraph.startsWith('> ')) {
                  return <blockquote key={index} className="content-quote">{paragraph.substring(2)}</blockquote>;
                }

                // Handle lists
                if (paragraph.startsWith('- ')) {
                  return <li key={index} className="content-list-item">{paragraph.substring(2)}</li>;
                }

                // Handle HTML formatted content (center, justify)
                if (paragraph.includes('<div style=')) {
                  return <div key={index} dangerouslySetInnerHTML={{ __html: paragraph }} />;
                }

                // Handle bold and italic
                let formattedParagraph = paragraph
                  .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')
                  .replace(/\*(.*?)\*/g, '<em>$1</em>');

                if (formattedParagraph.trim()) {
                  return <p key={index} dangerouslySetInnerHTML={{ __html: formattedParagraph }} />;
                }

                return null;
              })}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ArticleEditor;
