import React, { useState } from 'react';
import './Subscriptions.css';

interface SubscriptionPlan {
  id: string;
  name: string;
  frequency: string;
  price: number;
  originalPrice?: number;
  description: string;
  features: string[];
  popular?: boolean;
}

const subscriptionPlans: SubscriptionPlan[] = [
  {
    id: 'weekly',
    name: 'Weekly Fresh',
    frequency: 'Every Week',
    price: 24.99,
    description: 'Perfect for coffee enthusiasts who want fresh beans constantly',
    features: [
      '250g premium coffee beans',
      'New origin every week',
      'Brewing tips included',
      'Free shipping',
      'Cancel anytime'
    ]
  },
  {
    id: 'monthly',
    name: 'Monthly Explorer',
    frequency: 'Every Month',
    price: 89.99,
    description: 'Ideal balance of variety and convenience',
    features: [
      '1kg premium coffee (4 x 250g)',
      'Curated selection',
      'Detailed tasting notes',
      'Free shipping',
      'Access to exclusive blends',
      'Cancel anytime'
    ],
    popular: true
  },
  {
    id: 'annual',
    name: 'Annual Connoisseur',
    frequency: 'Full Year',
    price: 899.99,
    originalPrice: 1079.88,
    description: 'Best value for serious coffee lovers',
    features: [
      '12kg premium coffee (48 x 250g)',
      'Priority access to rare beans',
      'Exclusive annual blends',
      'Free express shipping',
      'Personal coffee consultant',
      'Cancel anytime'
    ]
  },
  {
    id: 'oneoff',
    name: 'One-Time Purchase',
    frequency: 'Single Order',
    price: 29.99,
    description: 'Try our coffee without commitment',
    features: [
      '250g premium coffee beans',
      'Choose your preferred origin',
      'Brewing guide included',
      'No subscription required'
    ]
  }
];

const Subscriptions: React.FC = () => {
  const [selectedPlan, setSelectedPlan] = useState<string>('');

  const handleSelectPlan = (planId: string) => {
    setSelectedPlan(planId);
  };

  const handleSubscribe = (plan: SubscriptionPlan) => {
    alert(`Thank you for choosing the ${plan.name} plan! This would redirect to payment processing.`);
  };

  return (
    <div className="subscriptions">
      <div className="container">
        <header className="subscriptions-header">
          <h1>Coffee Subscriptions</h1>
          <p>Choose the perfect plan for your coffee journey. All plans include premium, ethically-sourced beans delivered fresh to your door.</p>
        </header>

        <div className="plans-grid">
          {subscriptionPlans.map((plan) => (
            <div 
              key={plan.id}
              className={`plan-card ${plan.popular ? 'popular' : ''} ${selectedPlan === plan.id ? 'selected' : ''}`}
              onClick={() => handleSelectPlan(plan.id)}
            >
              {plan.popular && <div className="popular-badge">Most Popular</div>}

              <div className="plan-header">
                <h3>{plan.name}</h3>
                <div className="plan-frequency">{plan.frequency}</div>
              </div>

              <div className="plan-pricing">
                <div className="price">
                  <span className="currency">$</span>
                  <span className="amount">{plan.price}</span>
                  {plan.id !== 'oneoff' && <span className="period">/{plan.id === 'annual' ? 'year' : plan.id === 'monthly' ? 'month' : 'week'}</span>}
                </div>
                {plan.originalPrice && (
                  <div className="original-price">
                    Originally ${plan.originalPrice}
                  </div>
                )}
                {plan.originalPrice && (
                  <div className="savings">
                    Save ${(plan.originalPrice - plan.price).toFixed(2)}
                  </div>
                )}
              </div>

              <p className="plan-description">{plan.description}</p>

              <ul className="features-list">
                {plan.features.map((feature, index) => (
                  <li key={index}>
                    <span className="checkmark">‚úì</span>
                    {feature}
                  </li>
                ))}
              </ul>

              <button 
                className={`subscribe-btn ${selectedPlan === plan.id ? 'selected' : ''}`}
                onClick={(e) => {
                  e.stopPropagation();
                  handleSubscribe(plan);
                }}
              >
                {plan.id === 'oneoff' ? 'Buy Now' : 'Subscribe Now'}
              </button>
            </div>
          ))}
        </div>

        <section className="subscription-info">
          <div className="info-grid">
            <div className="info-item">
              <h3>üöö Free Shipping</h3>
              <p>All subscriptions include free shipping. Orders are processed within 1-2 business days.</p>
            </div>
            <div className="info-item">
              <h3>üìÖ Flexible Scheduling</h3>
              <p>Pause, skip, or modify your subscription anytime through your account dashboard.</p>
            </div>
            <div className="info-item">
              <h3>üåç Ethically Sourced</h3>
              <p>All our beans are ethically sourced and we work directly with farmers to ensure fair trade.</p>
            </div>
            <div className="info-item">
              <h3>üíØ Quality Guarantee</h3>
              <p>Not satisfied? We offer a 100% money-back guarantee on your first order.</p>
            </div>
          </div>
        </section>
      </div>
    </div>
  );
};

export default Subscriptions;
