import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Header.css';

interface HeaderProps {
  isDarkMode?: boolean;
  toggleDarkMode?: () => void;
  searchQuery?: string;
  onSearchChange?: (query: string) => void;
  onClearSearch?: () => void;
  showSearch?: boolean;
}

const Header: React.FC<HeaderProps> = ({ 
  isDarkMode = false, 
  toggleDarkMode,
  searchQuery = "",
  onSearchChange,
  onClearSearch,
  showSearch = false
}) => {
  const [subscribeEmail, setSubscribeEmail] = useState("");
  const [showEmailInput, setShowEmailInput] = useState(false);

  const handleSubscribeClick = () => {
    if (!showEmailInput) {
      setShowEmailInput(true);
    } else if (subscribeEmail.trim()) {
      // Navigate to subscriptions page with email pre-filled
      window.location.href = `/subscriptions?email=${encodeURIComponent(subscribeEmail)}`;
    }
  };

  const handleEmailKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && subscribeEmail.trim()) {
      handleSubscribeClick();
    }
  };

  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (onSearchChange) {
      onSearchChange(e.target.value);
    }
  };

  const clearSearch = () => {
    if (onClearSearch) {
      onClearSearch();
    }
  };
  return (
    <header className={`header ${isDarkMode ? 'dark-mode' : ''}`}>
      <div className="container">
        <div className="header-left">
          <div className="logo">
            <Link to="/blog">
              <h1>Dot Product</h1>
            </Link>
          </div>

          <nav className="nav-left">
            <ul className="nav-list">
              <li><Link to="/blog" className="nav-link">Blog</Link></li>
              <li><Link to="/dashboard" className="nav-link">Dashboard</Link></li>
              <li><Link to="/contact" className="nav-link">Contact</Link></li>
            </ul>
          </nav>
        </div>

        <div className="header-center">
          {showSearch && (
            <div className="header-search">
              <div className="search-input-wrapper">
                <input
                  type="text"
                  placeholder="Search articles..."
                  value={searchQuery}
                  onChange={handleSearchChange}
                  className="header-search-input"
                />
                {searchQuery && (
                  <button 
                    className="clear-search-btn"
                    onClick={clearSearch}
                    title="Clear search"
                  >
                    √ó
                  </button>
                )}
                <div className="search-icon">üîç</div>
              </div>
            </div>
          )}

          <div className="email-input-container">
            <input
              type="email"
              placeholder="Enter your email..."
              value={subscribeEmail}
              onChange={(e) => setSubscribeEmail(e.target.value)}
              onKeyPress={handleEmailKeyPress}
              className="subscribe-email-input"
            />
            <button 
              className="subscribe-submit-btn"
              onClick={handleSubscribeClick}
              disabled={!subscribeEmail.trim()}
            >
              ‚Üí
            </button>
          </div>
        </div>

        <div className="header-right">
          <Link to="/subscriptions" className="subscribe-btn">Subscribe</Link>
          <Link to="/donations" className="buy-coffee-btn">Buy Me Coffee ‚òï</Link>

          {toggleDarkMode && (
            <button 
              className="dark-mode-toggle"
              onClick={toggleDarkMode}
              title={isDarkMode ? 'Switch to Light Mode' : 'Switch to Dark Mode'}
            >
              {isDarkMode ? '‚òÄÔ∏è' : 'üåô'}
            </button>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
