import React, { useState, useRef, useEffect } from 'react';
import './ArticleEditor.css';

interface ArticleData {
  title: string;
  content: string;
  excerpt: string;
  category: string;
  author: string;
  image: string;
  imageUrl?: string;
  readTime: string;
}

interface ArticleEditorProps {
  onSave: (article: ArticleData) => void;
  onCancel: () => void;
  initialData?: Partial<ArticleData>;
}

const ArticleEditor: React.FC<ArticleEditorProps> = ({ onSave, onCancel, initialData }) => {
  const [article, setArticle] = useState<ArticleData>({
    title: '',
    content: '',
    excerpt: '',
    category: 'Engineering',
    author: 'Guest Author',
    image: '‚úçÔ∏è',
    imageUrl: '',
    readTime: '5 min read',
    ...initialData
  });

  const [isPreview, setIsPreview] = useState(false);
  const [imagePreview, setImagePreview] = useState<string>('');
  const [showImageInserter, setShowImageInserter] = useState(false);
  const [showFormatHelp, setShowFormatHelp] = useState(false);
  const [activeFormat, setActiveFormat] = useState<string>('');
  const [isDragging, setIsDragging] = useState(false);
  const [autoSave, setAutoSave] = useState(false);

  const textareaRef = useRef<HTMLTextAreaElement>(null);
  const contentImageInputRef = useRef<HTMLInputElement>(null);

  // Auto-save functionality
  useEffect(() => {
    if (autoSave) {
      const timer = setTimeout(() => {
        if (article.title.trim() && article.content.trim()) {
          // Could implement auto-save to localStorage here
          console.log('Auto-saving...', article);
        }
      }, 5000);
      return () => clearTimeout(timer);
    }
  }, [article, autoSave]);

  const categories = [
    'Engineering',
    'Systems',
    'Architecture',
    'Performance',
    'AI/ML',
    'Design',
    'Trends',
    'UI/UX'
  ];

  const handleInputChange = (field: keyof ArticleData, value: string) => {
    setArticle(prev => ({ ...prev, [field]: value }));
  };

  const handleImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const result = e.target?.result as string;
        setImagePreview(result);
        setArticle(prev => ({ ...prev, imageUrl: result }));
      };
      reader.readAsDataURL(file);
    }
  };

  const calculateReadTime = (content: string) => {
    const wordsPerMinute = 200;
    const words = content.trim().split(/\s+/).length;
    const minutes = Math.ceil(words / wordsPerMinute);
    return `${minutes} min read`;
  };

  const handleContentChange = (content: string) => {
    const readTime = calculateReadTime(content);
    setArticle(prev => ({ ...prev, content, readTime }));
  };

  const handleTextSelection = () => {
    const textarea = document.getElementById('content') as HTMLTextAreaElement;
    if (textarea) {
      const start = textarea.selectionStart;
      const end = textarea.selectionEnd;
      const selected = article.content.substring(start, end);
      setSelectedText(selected);
      setCursorPosition(start);
    }
  };

  const showFormatPreview = (format: string) => {
    switch (format) {
      case 'bold': return selectedText ? `**${selectedText}**` : '**Bold Text**';
      case 'italic': return selectedText ? `*${selectedText}*` : '*Italic Text*';
      case 'heading': return selectedText ? `## ${selectedText}` : '## Heading';
      case 'center': return selectedText ? `[Centered: ${selectedText}]` : '[Centered Text]';
      case 'justify': return selectedText ? `[Justified: ${selectedText}]` : '[Justified Text]';
      case 'quote': return selectedText ? `> ${selectedText}` : '> Quote Text';
      case 'list': return selectedText ? `‚Ä¢ ${selectedText}` : '‚Ä¢ List Item';
      default: return '';
    }
  };

  const insertFormatting = (format: string) => {
    const textarea = document.getElementById('content') as HTMLTextAreaElement;
    if (!textarea) return;

    const start = textarea.selectionStart;
    const end = textarea.selectionEnd;
    const selectedText = article.content.substring(start, end);
    const beforeText = article.content.substring(0, start);
    const afterText = article.content.substring(end);

    let newText = '';
    let cursorOffset = 0;

    switch (format) {
      case 'bold':
        newText = `**${selectedText}**`;
        cursorOffset = selectedText ? 0 : 2;
        break;
      case 'italic':
        newText = `*${selectedText}*`;
        cursorOffset = selectedText ? 0 : 1;
        break;
      case 'heading':
        newText = `## ${selectedText}`;
        cursorOffset = selectedText ? 0 : 3;
        break;
      case 'center':
        newText = `<div style="text-align: center">${selectedText}</div>`;
        cursorOffset = selectedText ? 0 : 30;
        break;
      case 'justify':
        newText = `<div style="text-align: justify">${selectedText}</div>`;
        cursorOffset = selectedText ? 0 : 31;
        break;
      case 'quote':
        newText = `> ${selectedText}`;
        cursorOffset = selectedText ? 0 : 2;
        break;
      case 'list':
        newText = `- ${selectedText}`;
        cursorOffset = selectedText ? 0 : 2;
        break;
      default:
        return;
    }

    const newContent = beforeText + newText + afterText;
    setArticle(prev => ({ ...prev, content: newContent }));

    // Reset cursor position
    setTimeout(() => {
      textarea.focus();
      textarea.setSelectionRange(start + newText.length - cursorOffset, start + newText.length - cursorOffset);
    }, 0);
  };

  const handleContentImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const result = e.target?.result as string;
        setContentImages(prev => [...prev, result]);
        insertImageIntoContent(result);
        setShowImageInserter(false);
      };
      reader.readAsDataURL(file);
    }
  };

  const insertImageIntoContent = (imageUrl: string) => {
    const textarea = document.getElementById('content') as HTMLTextAreaElement;
    if (!textarea) return;

    const start = textarea.selectionStart;
    const beforeText = article.content.substring(0, start);
    const afterText = article.content.substring(start);

    const imageMarkdown = `\n![Image](${imageUrl})\n`;
    const newContent = beforeText + imageMarkdown + afterText;

    setArticle(prev => ({ ...prev, content: newContent }));

    setTimeout(() => {
      textarea.focus();
      textarea.setSelectionRange(start + imageMarkdown.length, start + imageMarkdown.length);
    }, 0);
  };

  const handleSave = () => {
    if (article.title.trim() && article.content.trim()) {
      onSave(article);
    }
  };

  const isFormValid = article.title.trim() && article.content.trim() && article.excerpt.trim();

  return (
    <div className="article-editor">
      <div className="editor-header">
        <h2>Create New Article</h2>
        <div className="editor-actions">
          <button 
            className={`preview-btn ${isPreview ? 'active' : ''}`}
            onClick={() => setIsPreview(!isPreview)}
          >
            {isPreview ? 'üìù Edit' : 'üëÅÔ∏è Preview'}
          </button>
          <button className="cancel-btn" onClick={onCancel}>
            Cancel
          </button>
          <button 
            className="save-btn" 
            onClick={handleSave}
            disabled={!isFormValid}
          >
            üíæ Save Article
          </button>
        </div>
      </div>

      {!isPreview ? (
        <div className="editor-form">
          <div className="form-row">
            <div className="form-group">
              <label htmlFor="title">Title</label>
              <input
                id="title"
                type="text"
                value={article.title}
                onChange={(e) => handleInputChange('title', e.target.value)}
                placeholder="Enter article title..."
                className="title-input"
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="excerpt">Excerpt</label>
              <textarea
                id="excerpt"
                value={article.excerpt}
                onChange={(e) => handleInputChange('excerpt', e.target.value)}
                placeholder="Brief description of the article..."
                className="excerpt-input"
                rows={3}
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="category">Category</label>
              <select
                id="category"
                value={article.category}
                onChange={(e) => handleInputChange('category', e.target.value)}
                className="category-select"
              >
                {categories.map(cat => (
                  <option key={cat} value={cat}>{cat}</option>
                ))}
              </select>
            </div>
            <div className="form-group">
              <label htmlFor="author">Author</label>
              <input
                id="author"
                type="text"
                value={article.author}
                onChange={(e) => handleInputChange('author', e.target.value)}
                placeholder="Author name..."
                className="author-input"
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="emoji">Emoji (if no image)</label>
              <input
                id="emoji"
                type="text"
                value={article.image}
                onChange={(e) => handleInputChange('image', e.target.value)}
                placeholder="üìù"
                className="emoji-input"
                maxLength={2}
              />
            </div>
            <div className="form-group">
              <label htmlFor="image">Upload Image</label>
              <input
                id="image"
                type="file"
                accept="image/*"
                onChange={handleImageUpload}
                className="image-input"
              />
              {imagePreview && (
                <div className="image-preview">
                  <img src={imagePreview} alt="Preview" />
                </div>
              )}
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="content">Article Content</label>
            <div className="content-toolbar">
              <button type="button" onClick={() => insertFormatting('bold')} className="format-btn" title="Bold">
                <strong>B</strong>
              </button>
              <button type="button" onClick={() => insertFormatting('italic')} className="format-btn" title="Italic">
                <em>I</em>
              </button>
              <button type="button" onClick={() => insertFormatting('heading')} className="format-btn" title="Heading">
                H
              </button>
              <button type="button" onClick={() => insertFormatting('center')} className="format-btn" title="Center Align">
                ‚äû
              </button>
              <button type="button" onClick={() => insertFormatting('justify')} className="format-btn" title="Justify">
                ‚â°
              </button>
              <button type="button" onClick={() => insertFormatting('quote')} className="format-btn" title="Quote">
                "
              </button>
              <button type="button" onClick={() => insertFormatting('list')} className="format-btn" title="List">
                ‚Ä¢
              </button>
              <button type="button" onClick={() => setShowImageInserter(!showImageInserter)} className="format-btn image-btn" title="Insert Image">
                üñºÔ∏è
              </button>
            </div>
            {showImageInserter && (
              <div className="image-inserter">
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleContentImageUpload}
                  className="content-image-input"
                />
                <span className="image-inserter-text">Select an image to insert into the article</span>
              </div>
            )}
            <textarea
              id="content"
              value={article.content}
              onChange={(e) => handleContentChange(e.target.value)}
              placeholder="Write your article content here..."
              className="content-input"
              rows={15}
            />
            <div className="content-meta">
              <span className="read-time">{article.readTime}</span>
              <span className="word-count">{article.content.trim().split(/\s+/).length} words</span>
            </div>
          </div>
        </div>
      ) : (
        <div className="article-preview">
          <div className="preview-card">
            {article.imageUrl ? (
              <div className="preview-image" style={{ backgroundImage: `url(${article.imageUrl})` }} />
            ) : (
              <div className="preview-default-image">
                <span className="preview-emoji">{article.image}</span>
              </div>
            )}
            <div className="preview-content">
              <h3 className="preview-title">{article.title || 'Article Title'}</h3>
              <p className="preview-excerpt">{article.excerpt || 'Article excerpt will appear here...'}</p>
              <div className="preview-category">
                <span className="category-badge">{article.category}</span>
              </div>
              <div className="preview-meta">
                <span>{new Date().toLocaleDateString()}</span>
                <span>{article.readTime}</span>
                <span>By {article.author}</span>
              </div>
            </div>
          </div>
          <div className="preview-full-content">
            <h2>Full Article Content</h2>
            <div className="preview-article-content">
              {article.content.split('\n').map((paragraph, index) => {
                // Handle images
                if (paragraph.includes('![Image](')) {
                  const imageMatch = paragraph.match(/!\[Image\]\((.*?)\)/);
                  if (imageMatch) {
                    return (
                      <div key={index} className="content-image-container">
                        <img src={imageMatch[1]} alt="Article content" className="content-image" />
                      </div>
                    );
                  }
                }

                // Handle headings
                if (paragraph.startsWith('## ')) {
                  return <h3 key={index} className="content-heading">{paragraph.substring(3)}</h3>;
                }

                // Handle quotes
                if (paragraph.startsWith('> ')) {
                  return <blockquote key={index} className="content-quote">{paragraph.substring(2)}</blockquote>;
                }

                // Handle lists
                if (paragraph.startsWith('- ')) {
                  return <li key={index} className="content-list-item">{paragraph.substring(2)}</li>;
                }

                // Handle HTML formatted content (center, justify)
                if (paragraph.includes('<div style=')) {
                  return <div key={index} dangerouslySetInnerHTML={{ __html: paragraph }} />;
                }

                // Handle bold and italic
                let formattedParagraph = paragraph
                  .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')
                  .replace(/\*(.*?)\*/g, '<em>$1</em>');

                if (formattedParagraph.trim()) {
                  return <p key={index} dangerouslySetInnerHTML={{ __html: formattedParagraph }} />;
                }

                return null;
              })}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ArticleEditor;
