import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import ArticleEditor from '../components/ArticleEditor';
import './CreateArticle.css';

interface ArticleData {
  id: string;
  title: string;
  content: string;
  excerpt: string;
  category: string;
  author: string;
  image: string;
  imageUrl?: string;
  readTime: string;
  date: string;
  published: boolean;
  saved: boolean;
}

const CreateArticle: React.FC = () => {
  const navigate = useNavigate();
  const [showSuccessMessage, setShowSuccessMessage] = useState(false);

  const handleSaveArticle = (article: ArticleData) => {
    // Here you would typically send the article to your backend
    console.log('Saving article:', article);

    // For demo purposes, we'll save to localStorage
    const existingArticles = JSON.parse(localStorage.getItem('userArticles') || '[]');
    const newArticle = {
      ...article,
      id: Date.now().toString(),
      date: new Date().toISOString(),
      saved: false
    };

    existingArticles.push(newArticle);
    localStorage.setItem('userArticles', JSON.stringify(existingArticles));

    setShowSuccessMessage(true);

    // Auto-redirect after 2 seconds
    setTimeout(() => {
      navigate('/blog');
    }, 2000);
  };

  const handleCancel = () => {
    if (window.confirm('Are you sure you want to cancel? Any unsaved changes will be lost.')) {
      navigate('/blog');
    }
  };

  if (showSuccessMessage) {
    return (
      <div className="create-article-page">
        <div className="success-message">
          <div className="success-icon">âœ…</div>
          <h2>Article Saved Successfully!</h2>
          <p>Your article has been saved and will appear in the blog.</p>
          <p>Redirecting to blog...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="create-article-page">
      <ArticleEditor 
        onSave={handleSaveArticle}
        onCancel={handleCancel}
      />
    </div>
  );
};

export default CreateArticle;
