import React, { useState } from 'react';
import './Contact.css';

interface FormData {
  name: string;
  email: string;
  subject: string;
  message: string;
}

const Contact: React.FC = () => {
  const [formData, setFormData] = useState<FormData>({
    name: '',
    email: '',
    subject: '',
    message: ''
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitMessage, setSubmitMessage] = useState('');

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);

    // Simulate form submission
    setTimeout(() => {
      setSubmitMessage('Thank you for your message! We\'ll get back to you within 24 hours.');
      setFormData({ name: '', email: '', subject: '', message: '' });
      setIsSubmitting(false);
    }, 2000);
  };

  return (
    <div className="contact">
      <div className="container">
        <header className="contact-header">
          <h1>Get in Touch</h1>
          <p>We'd love to hear from you. Send us a message and we'll respond as soon as possible.</p>
        </header>

        <div className="contact-content">
          <div className="contact-info">
            <div className="info-section">
              <h3>📧 Email Us</h3>
              <p>hello@designdot.coffee</p>
              <p>support@designdot.coffee</p>
            </div>

            <div className="info-section">
              <h3>📞 Call Us</h3>
              <p>+1 (555) 123-4567</p>
              <p>Monday - Friday: 9AM - 6PM PST</p>
            </div>

            <div className="info-section">
              <h3>📍 Visit Us</h3>
              <p>123 Coffee Street<br />
              San Francisco, CA 94102<br />
              United States</p>
            </div>

            <div className="info-section">
              <h3>💬 Social Media</h3>
              <div className="social-links">
                <a href="#" className="social-link">Instagram</a>
                <a href="#" className="social-link">Twitter</a>
                <a href="#" className="social-link">Facebook</a>
              </div>
            </div>
          </div>

          <form className="contact-form" onSubmit={handleSubmit}>
            <h3>Send us a Message</h3>

            {submitMessage && (
              <div className="success-message">
                {submitMessage}
              </div>
            )}

            <div className="form-group">
              <label htmlFor="name">Name *</label>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                required
                placeholder="Your full name"
              />
            </div>

            <div className="form-group">
              <label htmlFor="email">Email *</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                required
                placeholder="your.email@example.com"
              />
            </div>

            <div className="form-group">
              <label htmlFor="subject">Subject *</label>
              <select
                id="subject"
                name="subject"
                value={formData.subject}
                onChange={handleInputChange}
                required
              >
                <option value="">Select a subject</option>
                <option value="subscription">Subscription Inquiry</option>
                <option value="billing">Billing Question</option>
                <option value="support">Customer Support</option>
                <option value="partnership">Partnership Opportunity</option>
                <option value="feedback">Feedback</option>
                <option value="other">Other</option>
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="message">Message *</label>
              <textarea
                id="message"
                name="message"
                value={formData.message}
                onChange={handleInputChange}
                required
                placeholder="Tell us how we can help you..."
                rows={6}
              />
            </div>

            <button 
              type="submit" 
              className="submit-btn"
              disabled={isSubmitting}
            >
              {isSubmitting ? 'Sending...' : 'Send Message'}
            </button>
          </form>
        </div>

        <section className="faq-section">
          <h2>Frequently Asked Questions</h2>
          <div className="faq-grid">
            <div className="faq-item">
              <h4>How do I modify my subscription?</h4>
              <p>You can easily modify your subscription through your account dashboard or by contacting our support team.</p>
            </div>
            <div className="faq-item">
              <h4>What if I don't like the coffee?</h4>
              <p>We offer a 100% satisfaction guarantee. If you're not happy with your order, contact us for a full refund or replacement.</p>
            </div>
            <div className="faq-item">
              <h4>Do you ship internationally?</h4>
              <p>Currently, we ship to the US and Canada. International shipping to other countries is coming soon!</p>
            </div>
            <div className="faq-item">
              <h4>How fresh is the coffee?</h4>
              <p>All our coffee is roasted to order and shipped within 48 hours of roasting to ensure maximum freshness.</p>
            </div>
          </div>
        </section>
      </div>
    </div>
  );
};

export default Contact;
