import React, { useState } from 'react';
import './Blog.css';

interface BlogPost {
  id: number;
  title: string;
  excerpt: string;
  content: string;
  author: string;
  date: string;
  category: string;
  readTime: string;
  image: string;
}

const blogPosts: BlogPost[] = [
  {
    id: 1,
    title: "The Art of Pour-Over Coffee: A Beginner's Guide",
    excerpt: "Discover the fundamentals of pour-over brewing and how to create the perfect cup at home.",
    content: "Pour-over coffee is more than just a brewing method ‚Äì it's a ritual that connects you with your coffee on a deeper level. The controlled pouring technique allows you to extract the best flavors from your beans while creating a meditative morning routine.",
    author: "Sarah Johnson",
    date: "2024-01-15",
    category: "Brewing",
    readTime: "5 min read",
    image: "üåä"
  },
  {
    id: 2,
    title: "Ethiopian Coffee: Exploring the Birthplace of Coffee",
    excerpt: "Journey through Ethiopia's coffee regions and discover the unique flavors that started it all.",
    content: "Ethiopia is widely considered the birthplace of coffee, with legends dating back to the 9th century. Today, Ethiopian coffee continues to captivate coffee lovers with its bright, floral notes and complex flavor profiles that vary dramatically by region.",
    author: "Michael Chen",
    date: "2024-01-12",
    category: "Origins",
    readTime: "7 min read",
    image: "üåç"
  },
  {
    id: 3,
    title: "Sustainable Coffee: Why Fair Trade Matters",
    excerpt: "Understanding the impact of ethical sourcing on farmers, communities, and coffee quality.",
    content: "Fair trade coffee ensures that farmers receive fair compensation for their hard work while promoting sustainable farming practices. When you choose ethically sourced coffee, you're not just getting a better cup ‚Äì you're supporting entire communities.",
    author: "Emma Rodriguez",
    date: "2024-01-10",
    category: "Sustainability",
    readTime: "6 min read",
    image: "üå±"
  },
  {
    id: 4,
    title: "Coffee Cupping: How to Taste Like a Professional",
    excerpt: "Learn the art of coffee cupping and develop your palate like a professional coffee taster.",
    content: "Coffee cupping is the professional method for evaluating coffee quality and flavor characteristics. By learning this technique, you'll develop a more sophisticated palate and better understand what makes each coffee unique.",
    author: "David Thompson",
    date: "2024-01-08",
    category: "Tasting",
    readTime: "8 min read",
    image: "üëÖ"
  },
  {
    id: 5,
    title: "The Science Behind Coffee Roasting",
    excerpt: "Explore the chemical reactions that transform green coffee beans into the aromatic brown beans we love.",
    content: "Coffee roasting is a complex process involving hundreds of chemical reactions. Understanding these transformations helps explain why different roast levels produce such dramatically different flavors and aromas.",
    author: "Dr. Lisa Park",
    date: "2024-01-05",
    category: "Science",
    readTime: "10 min read",
    image: "üî¨"
  },
  {
    id: 6,
    title: "Cold Brew vs. Iced Coffee: What's the Difference?",
    excerpt: "Clarifying the confusion between cold brew and iced coffee, and when to choose each.",
    content: "While both are served cold, cold brew and iced coffee are fundamentally different beverages. Cold brew uses time instead of heat to extract flavors, resulting in a smoother, less acidic drink that's perfect for hot summer days.",
    author: "Alex Martinez",
    date: "2024-01-03",
    category: "Brewing",
    readTime: "4 min read",
    image: "üßä"
  }
];

const categories = ["All", "Brewing", "Origins", "Sustainability", "Tasting", "Science"];

const Blog: React.FC = () => {
  const [selectedCategory, setSelectedCategory] = useState("All");
  const [selectedPost, setSelectedPost] = useState<BlogPost | null>(null);

  const filteredPosts = selectedCategory === "All" 
    ? blogPosts 
    : blogPosts.filter(post => post.category === selectedCategory);

  const handlePostClick = (post: BlogPost) => {
    setSelectedPost(post);
  };

  const handleBackClick = () => {
    setSelectedPost(null);
  };

  if (selectedPost) {
    return (
      <div className="blog">
        <div className="container">
          <button className="back-btn" onClick={handleBackClick}>
            ‚Üê Back to Blog
          </button>
          <article className="blog-post-full">
            <header className="post-header">
              <div className="post-icon">{selectedPost.image}</div>
              <h1>{selectedPost.title}</h1>
              <div className="post-meta">
                <span className="author">By {selectedPost.author}</span>
                <span className="date">{new Date(selectedPost.date).toLocaleDateString()}</span>
                <span className="category">{selectedPost.category}</span>
                <span className="read-time">{selectedPost.readTime}</span>
              </div>
            </header>
            <div className="post-content">
              <p className="lead">{selectedPost.excerpt}</p>
              <p>{selectedPost.content}</p>
              <p>This is a preview of our blog post content. In a full implementation, this would contain the complete article with proper formatting, images, and more detailed content.</p>
            </div>
          </article>
        </div>
      </div>
    );
  }

  return (
    <div className="blog">
      <div className="container">
        <header className="blog-header">
          <h1>Coffee Blog</h1>
          <p>Insights, tips, and stories from the world of coffee</p>
        </header>

        <div className="category-filter">
          {categories.map(category => (
            <button
              key={category}
              className={`category-btn ${selectedCategory === category ? 'active' : ''}`}
              onClick={() => setSelectedCategory(category)}
            >
              {category}
            </button>
          ))}
        </div>

        <div className="blog-grid">
          {filteredPosts.map(post => (
            <article 
              key={post.id} 
              className="blog-card"
              onClick={() => handlePostClick(post)}
            >
              <div className="card-icon">{post.image}</div>
              <div className="card-content">
                <h3>{post.title}</h3>
                <p className="excerpt">{post.excerpt}</p>
                <div className="card-meta">
                  <span className="author">{post.author}</span>
                  <span className="date">{new Date(post.date).toLocaleDateString()}</span>
                </div>
                <div className="card-footer">
                  <span className="category">{post.category}</span>
                  <span className="read-time">{post.readTime}</span>
                </div>
              </div>
            </article>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Blog;
